create table goods(
   id mediumint unsigned not null auto_increment comment 'Id',
   goods_name varchar(1500) not null comment '产品名称',
   logo varchar(150) not null default '' comment '产品logo',
   big_logo varchar(150) not null default '' comment '产品logo大图',
   mid_logo varchar(150) not null default '' comment '产品logo中图',
   sm_logo varchar(150) not null default '' comment '产品logo小图',
   goods_msc varchar(500) not null comment '产品简介',
   goods_td varchar(300) not null comment '产品特点',
   goods_desc varchar(3000) not null comment '产品特点描述',
   addtime datetime not null comment '添加时间',
   primary key (id),   
   key addtime(addtime)
)engine=InnoDB default charset=utf8 comment '产品';
INSERT INTO `goods` (`id`, `goods_name`, `logo`, `big_logo`, `mid_logo`, `sm_logo`, `goods_msc`, `goods_td`, `goods_desc`, `addtime`) VALUES (1, 'GaN-on-Silicon', 'Goods/logo/2016-06-12/575d64db8588f.png', 'Goods/logo/2016-06-12/big_575d64db8588f.png', 'Goods/logo/2016-06-12/mid_575d64db8588f.png', 'Goods/logo/2016-06-12/sm_575d64db8588f.png', 'Enkris is among the few companies which is capable to supply 200 mm GaN-on-Si wafers to its customers in volume.', 'Key features of GaN-on-Si epitaxial wafers', '<p><span><br /></span></p><ul><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">High breakdown voltage and extremely low buffer leakage</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">Excellent uniformity and reproducibility</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">High electron density, high electron mobility and thus low sheet resistance</span></p></li></ul><p><br /></p>', '2016-6-14 23:09:34');
INSERT INTO `goods` (`id`, `goods_name`, `logo`, `big_logo`, `mid_logo`, `sm_logo`, `goods_msc`, `goods_td`, `goods_desc`, `addtime`) VALUES (2, 'GaN-on-SiC', 'Goods/logo/2016-06-12/575d657989836.png', 'Goods/logo/2016-06-12/big_575d657989836.png', 'Goods/logo/2016-06-12/mid_575d657989836.png', 'Goods/logo/2016-06-12/sm_575d657989836.png', 'GaN-on-SiC epi-wafers are available from 2 inches to 4 inches.', 'Key features of GaN-on-SiC epitaxial wafers', '<p><br /></p><ul><li><p><span style=\"font-size:12px;font-family:sans-serif, Arial, Verdana;\">Extremely low leakage current;</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">Very high mobility &gt;2200cm2 /Vs;</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">Atomically smooth surface morphology</span></p></li></ul><p><br /></p>', '2016-6-14 23:09:00');
INSERT INTO `goods` (`id`, `goods_name`, `logo`, `big_logo`, `mid_logo`, `sm_logo`, `goods_msc`, `goods_td`, `goods_desc`, `addtime`) VALUES (3, 'GaN-on-Sapphire', 'Goods/logo/2016-06-12/575d6655ed45e.png', 'Goods/logo/2016-06-12/big_575d6655ed45e.png', 'Goods/logo/2016-06-12/mid_575d6655ed45e.png', 'Goods/logo/2016-06-12/sm_575d6655ed45e.png', 'GaN-on-Sapphire epi-wafers are available from 2 inches to 4 inches.', 'Key features of GaN-on-Sapphire epitaxial wafers', '<p><br /></p><ul><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">High breakdown voltage and extremely low buffer leakage;</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">Excellent uniformity and reproducibility;</span></p></li><li><p><span style=\"font-family:sans-serif, Arial, Verdana;\">High electron density, high electron mobility and thus low sheet resistance</span></p></li></ul><p><br /></p>', '2016-6-14 23:08:03');
INSERT INTO `goods` (`id`, `goods_name`, `logo`, `big_logo`, `mid_logo`, `sm_logo`, `goods_msc`, `goods_td`, `goods_desc`, `addtime`) VALUES (4, 'Customized wafers', 'Goods/logo/2016-06-14/5760241c7f8b3.png', 'Goods/logo/2016-06-14/big_5760241c7f8b3.png', 'Goods/logo/2016-06-14/mid_5760241c7f8b3.png', 'Goods/logo/2016-06-14/sm_5760241c7f8b3.png', '', '', '', '2016-6-14 23:34:52');


