   id mediumint unsigned not null auto_increment comment 'Id',
   cate varchar(150) not null comment '职位名称',
   category varchar(150) not null comment '职位类型',
   position varchar(150) not null comment '类型',
   location varchar(300) not null comment '地址',
   kr mediumtext not null comment '主要职责',
   eq mediumtext not null comment '任职条件', 
   addtime datetime not null comment '添加时间',
   primary key (id),   
   key addtime(addtime)
)engine=InnoDB default charset=utf8 comment '招聘信息';
INSERT INTO `employ` (`id`, `category`, `position`, `location`, `kr`, `eq`, `addtime`, `cate`) VALUES (1, 'Engineering', 'Full Time', 'Suzhou Industry Park,\r\nJiangsu Province,P.R.China', '<ol><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍Taking ownership of MOCVD reactors and processes including scheduling, data analysis, layer characterization;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">leading process-improvement and coordinating process transfers to production;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Perform hardware characterization on MOCVD system, directing equipment maintenance, within safety guidelines;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Troubleshoot technical problems, perform Root Cause Analysis and resolve process engineering issues;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Generating internal documentation for products, and technical reports;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Implement new technology, products and analytical instrumentation;‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍</span></p></li></ol><p><span>‍</span><span>‍</span><span>‍</span><span>‍</span><span>‍</span><span>‍</span><br /></p>', '<ol><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">A MS degree or above in Electrical Engineering, Materials Science, or Chemical Engineering;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Technical experience of thin Film deposition, epitaxy or MOCVD;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Team-work spirit;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Excellent communication and interpersonal skills are preferred; </span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Knowledge of experimental design is a plus</span></p></li></ol><p><br /></p>', '2016-6-15 19:46:13', '2 MCVD Epitaxial Engineers');
INSERT INTO `employ` (`id`, `category`, `position`, `location`, `kr`, `eq`, `addtime`, `cate`) VALUES (2, 'test engine', 'full time', 'Suzhou Industry Park, Jiangsu Province,P.R.China', '<ol><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍Taking ownership of MOCVD reactors and processes including scheduling, data analysis, layer characterization;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">leading process-improvement and coordinating process transfers to production;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Perform hardware characterization on MOCVD system, directing equipment maintenance, within safety guidelines;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Troubleshoot technical problems, perform Root Cause Analysis and resolve process engineering issues;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Generating internal documentation for products, and technical reports;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Implement new technology, products and analytical instrumentation;‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍‍</span></p></li></ol><p><span style=\"font-family:sans-serif;font-size:16px;\"></span></p><p><br /><span style=\"font-size:12px;\"></span></p>', '<ol><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">A MS degree or above in Electrical Engineering, Materials Science, or Chemical Engineering;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Technical experience of thin Film deposition, epitaxy or MOCVD;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Team-work spirit;</span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Excellent communication and interpersonal skills are preferred; </span></p></li><li><p><span style=\"font-family:arial, helvetica, sans-serif;\">Knowledge of experimental design is a plus</span></p></li></ol><p><br /></p>', '2016-6-15 19:54:37', '3 XRD    Test Engineers');

create table intro(
   id mediumint unsigned not null auto_increment comment 'Id',
   logo varchar(150) not null comment 'Logo',
   cintro varchar(3000) not null comment '公司简介',
   location varchar(200) not null comment '地址',
   zip varchar(10) not null comment '邮编',
   gintro varchar(3000) not null comment '产品简介',
   primary key (id)
)engine=InnoDB default charset=utf8 comment '简介';
INSERT INTO `intro` (`id`, `logo`, `cintro`, `location`, `zip`, `gintro`) VALUES (2, 'Intro/2016-06-07/5756ec2ebc0b7.png', '<p>Energy is one of the main concerns in our current society. Enkris is dedicated to developing high quality GaN epi-wafers for high efficiency GaN electronics and thus reduce the overall electricity consumption. GaN-based devices can achieve low specific Ron and fast switching/oscillation simultaneously because of its high critical electrical field. Enkris’ wafers have been optimized with extremely low buffer leakage and low buffer traps, which are the key features for high performance GaN devices.</p>', 'NW-20, Nanopolis Suzhou, 99 Jinji Avenue, Suzhou Industrial Park, China', '215123', '<p>Enkris Semiconductor is a startup company focused on green technology using GaN. GaN is a wide-bandgap material which can greatly reduce the electricity loss during power conversion and RF signal generation. Enkris is dedicated to developing and producing high quality GaN epi-wafers for the next generation high efficient power electronic devices and RF devices. Enkris offers GaN-on-Sapphire, GaN-on-SiC and GaN-on-Silicon wafers with varied wafer sizes to its customers.</p>');


